mapper.select:
select acc_idx, acc_name, acc_img, acc_text, acc_addr1, acc_addr2, cnt, acc_type, region_name from ( select rownum AS RNO, acc_idx, acc_name, acc_img, acc_text, acc_addr1, acc_addr2, cnt, acc_type , region_name, region_shortname from ( select distinct acc_idx, acc_name, acc_img, acc_text, acc_addr1, acc_addr2, cnt, acc_type , region_name, region_shortname from ( select acc_idx, acc_name, acc_img, acc_text, acc_addr1, acc_addr2, cnt, acc_type , region_name, region_shortname, acc_grade from view_hotel_list_search 1 = 1
and ((ay_fee * #{adultNum}) + (k_fee * #{kidsNum}) between #{accListPrice1} and #{accListPrice2})
and
acc_addr1 like '%' || '${regionArr[i]}' || '%' or acc_addr2 like '%' || '${regionArr[i]}' || '%'
and (book_start NOT between TO_DATE(#{book_start},'YYYY-MM-DD HH24:MI:SS') and TO_DATE(#{book_end}, 'YYYY-MM-DD HH24:MI:SS'))
and (book_end NOT between TO_DATE(#{book_start},'YYYY-MM-DD HH24:MI:SS') and TO_DATE(#{book_end}, 'YYYY-MM-DD HH24:MI:SS'))
and
acc_grade = ${acc_gradeArr[j]}
and
acc_type like '%'|| '${acc_typeArr[k]}' || '%'
) V
where acc_addr1 like '%'|| #{blendSearchWord} || '%' or acc_name like '%'|| #{blendSearchWord} || '%' or region_shortname like '%' || #{blendSearchWord} || '%'
) T ) F where RNO between #{startRno} and #{endRno}
